markup.content:
---
import {format} from 'date-fns';

const title = "Astro";

const builtAt: Date = new Date();
const builtAtFormatted = format(builtAt, 'MMMM dd, yyyy -- H:mm:ss.SSS');
---

<div class="flex flex-col justify-center items-center m-6">
  <h1 class="mb-4 text-4xl font-bold text-gray-900">Hello, {title}!</h1>
  <img class="w-32" alt="logo" src="https://livecodes.io/livecodes/assets/templates/astro.svg" />
  <p>You clicked <span id="counter">0</span> times.</p>
  <button id="counter-button" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 m-2 focus:outline-none">Click me</button>
  <p class="m-3 p-3 border border-gray-300 rounded bg-gray-100">
    <strong>RENDERED AT:</strong><br/>
    {builtAtFormatted}
  </p>
</div>

script.content:
import confetti from 'canvas-confetti';

let count = 0;
document
  .querySelector<HTMLButtonElement>('#counter-button')!
  .addEventListener('click', () => {
    count += 1;
    document.querySelector<HTMLElement>('#counter')!.innerText = String(count);
    confetti();
  });
